import React, { useState } from 'react';
import CSVReader from 'react-csv-reader';
import { convertToWorkerObjects } from '../utils/convertToWorkerObjects';

const CreateWorkers = () => {
  const [workers, setWorkers] = useState([]);
  const [responseStatus, setResponseStatus] = useState('');

  const handleFileLoad = (data) => {
    // Convert CSV data to worker objects
    const workerObjects = convertToWorkerObjects(data);
    setWorkers(workerObjects);
  };

  // OFFICAL HANDLE CREATE WORKERS
  const handleCreateWorkers = async () => {
    // Make POST request to Twilio Function
    const response = await fetch(
      'https://ns-throttling-functions-2997.twil.io/create-csv-workers',
      {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(workers),
      }
    );
    const json = await response.json();
    console.log(json.message);
    setResponseStatus(json.message);
  };

  //SIMPLE POST EXAMPLE
  // const data = {
  //   key1: 'value1',
  //   key2: 'value2',
  // };

  // const handleCreateWorkers = async () => {
  //   const response = await fetch(
  //     'https://ns-throttling-functions-2997.twil.io/simple-post-example',
  //     {
  //       method: 'POST',
  //       headers: {
  //         'Content-Type': 'application/json',
  //       },
  //       body: JSON.stringify(data),
  //     }
  //   );
  //   const json = await response.json();
  //   console.log(json.message);
  // };

  // SIMPLE GET EXAMPLE CALL
  // const handleCreateWorkers = async () => {
  //   const response = await fetch(
  //     'https://ns-throttling-functions-2997.twil.io/simple-get-example',
  //     {
  //       method: 'GET',
  //     }
  //   );
  //   const json = await response.json();
  //   console.log(json.message);
  // };

  return (
    <div>
      <CSVReader onFileLoaded={handleFileLoad} />
      <button onClick={handleCreateWorkers}>Create Workers</button>
      {responseStatus && <div>{responseStatus}</div>}
    </div>
  );
};

export default CreateWorkers;
