export const convertToWorkerObjects = (data) => {
  // Define the header row, which will be used to map the data to the correct keys
  const headers = [
    'friendlyName',
    'contact_uri',
    'full_name',
    'email',
    'roles',
    'skills',
    'language',
  ];

  // Skip the first row
  let firstRow = true;

  // Convert the CSV data to worker objects
  const workerObjects = data.map((row) => {
    if (firstRow) {
      firstRow = false;
      return;
    }

    const worker = {};
    worker.attributes = {};
    headers.forEach((header, index) => {
      if (header === 'roles' || header === 'skills') {
        // Split comma-separated values into arrays
        worker.attributes[header] = row[index].split(',');
      } else if (header === 'friendlyName') {
        worker[header] = row[index];
      } else {
        worker.attributes[header] = row[index];
      }
    });
    return worker;
  });

  return workerObjects.filter((worker) => worker);
};
